package operation;

public class Example01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=4; //선언과 대입을 동시에 하는 것을 '초기화'라고 한다.
		int y=x*4+3;
		
		System.out.println(x);
		System.out.println(y);
		
		int z=5+3*4;
		System.out.println(z);
	}

}
